import React, { useState, useEffect } from 'react';
// import { Switch, Route } from "react-router-dom"
// import Navbar from "./components/Header"
// import Search from "./components/pages/Search"
import Cards from "./components/Cards"
// import SelectedCard from "./components/SelectedCard"
import { Container, Col, Row } from "reactstrap";
// import axios from "axios";
import ReactMapGL, { Marker } from "react-map-gl"
import 'mapbox-gl/dist/mapbox-gl.css';
import API from "./utils/API"
import Points from './components/Points'
import image from "./components/img/marker.jpg"


// console.log(API.searchWeather(12,30))


function App() {
  const [coordinates, setCoordinates] = useState([])

  const [viewport, setViewPort] = useState({
    latitude: 37.7577,
    longitude: -122.4376,
    zoom: 5,
    width: '100vw',
    height: '60vh'
  })

  useEffect(() => {
    loadWeather()
  }, [])

  function loadWeather(lat, lon) {
    API.searchWeather(15.8700, 100.9925)
      // .then(res => res.data.coord)
      .then(res => console.log(res))
      // .then(data => setCoordinates([data.lat, data.lon]))

  }

  return (
    <Container fluid={true}>
      <Row>
        <Col>
          {/* <Navbar /> */}

        </Col>
      </Row>
      <Row>
        <Col>
          <ReactMapGL
            {...viewport}
            mapStyle={"mapbox://styles/suarez9093/ck773vm2e08i91iqi1m48zfss"}
            mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_API_KEY}
            onViewportChange={setViewPort}
          >

            <Marker latitude={37.78} longitude={-122.41} offsetLeft={-20} offsetTop={-10}>
              <button>

             <img className="pointer" src={image} alt="pointer"/>
              </button>
            </Marker>

          </ReactMapGL>
        </Col>
      </Row>
        <Col>
          <Cards className="border" />
        </Col>
      <Row>

      </Row>
    </Container>

  );
}

export default App;
