import React from 'react';
import { Marker } from "react-map-gl"
import image from "./img/marker.jpg"

function MarkerImage({location, onMarkerSelect}){

    function handleClick(location){
        // alert('clicked')
       const point = location.find(id => console.log(id))
        console.log('id:', point)
       }

    return(
        <div>
            {location.map(l => (
              <Marker 
              key={l.id}
              latitude={l.coord.lat} 
              longitude={l.coord.lon} 
              offsetLeft={-20} 
              offsetTop={-10}>
              <button onClick={() => handleClick(location)}>
                <img className="pointer" src={image} alt="pointer" />
              </button>
            </Marker>
            ))}
        </div>

    )
}

export default MarkerImage
