import React, { useState, useEffect } from 'react';
// import { Switch, Route } from "react-router-dom"
// import Navbar from "./components/Header"
// import Search from "./components/pages/Search"
import Cards from "./components/Cards"
// import SelectedCard from "./components/SelectedCard"
import { Container, Col, Row } from "reactstrap";
import ReactMapGL, { Marker } from "react-map-gl"
import 'mapbox-gl/dist/mapbox-gl.css';
import API from "./utils/API"
import Points from './components/Points'
import image from "./components/img/marker.jpg"
import SearchBar from "./components/Searchbar"
import WeatherDetail from "./components/WeatherDetail"


function App() {
  const [location, setLocation] = useState([])
  const [ selectedMarker, setSelectedMarker ] = useState()
  const [viewport, setViewPort] = useState({
    latitude: 37.7577,
    longitude: -122.4376,
    zoom: 3,
    width: '100vw',
    height: '60vh'
  })

  useEffect(() => {
    loadWeather()
  }, [])

  function loadWeather() {
    API.searchWeather(55.5, 37.5, 10)
      .then(res => res.data.list)
      .then(data => setLocation(data))
      console.log(location)
  }

  function handleClick(e){
   alert('clicked')
  }

  return (
    <Container fluid={true}>
      <Row>
        <Col>
          <SearchBar />
        </Col>
      </Row>
      <Row>
        <Col>
          <ReactMapGL
            {...viewport}
            mapStyle={"mapbox://styles/suarez9093/ck773vm2e08i91iqi1m48zfss"}
            mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_API_KEY}
            onViewportChange={setViewPort}
          >
            {location.map(data => (
              <Marker 
                key={data.id}
                latitude={data.coord.lat} 
                longitude={data.coord.lon} 
                offsetLeft={-20} 
                offsetTop={-10}>

                  <img className="pointer" src={image} alt="pointer" />

              </Marker>

            ))}
          </ReactMapGL>
        </Col>
      </Row>
      <Col>
        {/* <Cards className="border" /> */}
        {location.map(data => console.log(data))}
      </Col>
      <Row>
        <Col>
        <WeatherDetail selectedMarker={selectedMarker}/>
        </Col>
      </Row>
    </Container>

  );
}

export default App;
