import React, { useState, useEffect } from 'react';
import API from '../utils/API'
import {
    Card, CardText, CardBody, CardTitle, CardSubtitle, Button, CardHeader
} from 'reactstrap';
import NavbarComponent from '../components/NavbarComponent'
import { DayWrapper } from "../styles/index.js";


function SavedWeatherPoints() {
    const [savedWeather, setSavedWeather] = useState()

    useEffect(() => {
        loadWeather()
    }, [])

    function loadWeather() {
        API.getAllWeather()
            .then(res => {
                //  console.log('saved to state: ',res.data)
                setSavedWeather(res.data)
            })
    }

    function handleDeleteWeather(id) {
        API.deleteWeather(id).then(res => {
            loadWeather()
        })
        alert('Deleted!')

    };



    // console.log('savedWeather: ', typeof savedWeather)
    return (
        <div>
            <NavbarComponent/>
            {!savedWeather ? <h1>No saved Results</h1> :
                savedWeather.map(weather => (
               <DayWrapper  key={weather._id}>
                <Card>
                    <CardHeader></CardHeader>
                    <CardBody>
                        <h3>Country: {weather.country}</h3>
                        <h3>City: {weather.city}</h3>
                        {/* <img src={`${process.env.PUBLIC_URL}/icons/${weather.icon}.png`} alt={weather.description} /> */}
                        <p><strong>High:</strong> {weather.temp.toFixed(1)}°</p>
                        <p><strong>Low:</strong> {weather.temp}°</p>
                        <p><strong>Precip:</strong> {weather.temp}%</p>
                        <Button className="btn" color="danger" onClick={() => handleDeleteWeather(weather._id)}>Delete this weather point</Button>
                    </CardBody>
                </Card>
            </DayWrapper>
                ))}
        </div>
    )
}

export default SavedWeatherPoints
