import React, { useState } from 'react';
import { Container, Col, Row } from "reactstrap";
import ReactMapGL from "react-map-gl"
import 'mapbox-gl/dist/mapbox-gl.css';
import API from "./utils/API"
import SearchBar from "./components/Searchbar"
import MarkerWeatherDetail from "./components/MarkerWeatherDetail"
import MarkerImage from "./components/MarkerImage"

function App() {
  // State
  // =====================================================
  const [selectedMarker, setSelectedMarker] = useState()
  const [markerCount, setMarkerCount] = useState()
  const [searchField, setSearchField] = useState()

  const [viewport, setViewPort] = useState({
    latitude: 35.5501, // needs to be dynamic
    longitude: -100.7821, // dynamic
    zoom: 3,
    width: '100vw',
    height: '60vh'
  })



  // Functions
  // =====================================================

  function handleInputChange(e) {
      const { value } = e.target
      setSearchField(value)
  }


  function handleFormSubmit(e) {
    e.preventDefault()
    const { value } = e.target
    console.log('value',value)
    setSearchField(value)
    // API.searchWeather( searchField).then(res => setMarkerCount(res.data.list))
    setSearchField('')
    setViewPortCoordinates()
  }

  function onMarkerSelect(marker) {
    setSelectedMarker(marker)
  }

  function setViewPortCoordinates(lat,lon,searchField){
    API.searchWeather(lat,lon,searchField).then(res => setMarkerCount(res.data.list))
  }

  // Content
  // =====================================================
  return (
    <Container fluid={true}>
      <Row>
        <Col>
          <SearchBar
            handleFormSubmit={handleFormSubmit}
            handleInputChange={handleInputChange}
            searchField={searchField}
          />
        </Col>
      </Row>
      <Row>
        <Col>
          <ReactMapGL
            {...viewport}
            mapStyle={"mapbox://styles/suarez9093/ck773vm2e08i91iqi1m48zfss"}
            mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_API_KEY}
            onViewportChange={setViewPort}
          >
            <MarkerImage
              onMarkerSelect={onMarkerSelect}
              markerCount={markerCount}
            />

          </ReactMapGL>
        </Col>
      </Row>

      <Row>
        <Col>
          <MarkerWeatherDetail selectedMarker={selectedMarker} />
        </Col>
      </Row>
    </Container>

  );
}

export default App;
